const cars=new Swiper(".cars .swiper-container",{loop:!0,speed:400,spaceBetween:50,navigation:{nextEl:".cars__arrow_next",prevEl:".cars__arrow_prev"}});document.querySelectorAll(".work__button:nth-child(1)").forEach(e=>e.onclick=()=>cars.slidePrev()),document.querySelectorAll(".work__button:nth-child(2)").forEach(e=>e.onclick=()=>cars.slideNext());